sourceCode:
/* ==UserStyle==

@name YouTube Catppuccin
@namespace github.com/catppuccin/youtube
@homepageURL https://github.com/catppuccin/youtube
@version 2.2.3
@updateURL https://github.com/catppuccin/youtube/raw/main/src/catppuccin.user.css
@description Soothing pastel theme for YouTube
@author isabelroses
@license MIT

@preprocessor less

@var select lightTheme "Light Theme" ["latte:Latte*", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkTheme "Dark Theme" ["latte:Latte", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColour  "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red*", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Grey"]

@var checkbox joinColor "Use dark join button" 0
@var checkbox subscribeColor "Use dark subscribe button" 0
@var checkbox oledOn "Enable black bars" 0

==/UserStyle== */
@-moz-document regexp('https?:\\/\\/(?!gaming\\.)(?!consent\\.)(?!vr\\.)(?!music\\.)(?!tv\\.)(?!artists\\.)(?!studio\\.).*youtube(?!kids\\.).*\\.com\\/(?!premium|howyoutubeworks).*$') {

    /* init */
    :root[dark] {
        #catppuccin(@darkTheme, @accentColour);
        #subscribe-color(@subscribeColor);
        #join-color(@joinColor);
    }
    :root {
        #catppuccin(@lightTheme, @accentColour);
        #subscribe-color(@subscribeColor);
        #join-color(@joinColor);
    }
    #oled(@oledOn);
    @shadow: 0 1px 0.5px rgba(0, 0, 0, .13);

    /* buttons look nicer */
    #join-color(@join-color) when (@join-color =1) {
        .yt-spec-button-shape-next--mono[aria-label="Join this channel"] .yt-spec-button-shape-next--mono[aria-label="Join this channel"]:hover {
            background-color: @mantle !important;
            border-color: @mantle !important;
            color: @text !important;
        }
    }

    #subscribe-color(@subscribe-color) when (@subscribe-color =1) {
        .ytp-sb-subscribe,
        a.ytp-sb-subscribe,
        .yt-spec-button-shape-next[aria-label^="Subscribe"],
        .yt-spec-button-shape-next[aria-label^="Subscribe"]:hover {
            background-color: @mantle !important;
            border-color: @mantle !important;
            box-shadow: @shadow !important;
            color: @text !important;
        }
    }

    /* for oled monitors */
    #oled(@oled) when (@oled =1) {
        #ytd-player #container {
            background: #000 !important;
        }
    }

    @catppuccin: {
        @latte: {
            @rosewater: #dc8a78;
            @flamingo: #dd7878;
            @pink: #ea76cb;
            @mauve: #8839ef;
            @red: #d20f39;
            @maroon: #e64553;
            @peach: #fe640b;
            @yellow: #df8e1d;
            @green: #40a02b;
            @teal: #179299;
            @sky: #04a5e5;
            @sapphire: #209fb5;
            @blue: #1e66f5;
            @lavender: #7287fd;
            @text: #4c4f69;
            @subtext1: #5c5f77;
            @subtext0: #6c6f85;
            @overlay2: #7c7f93;
            @overlay1: #8c8fa1;
            @overlay0: #9ca0b0;
            @surface2: #acb0be;
            @surface1: #bcc0cc;
            @surface0: #ccd0da;
            @base: #eff1f5;
            @mantle: #e6e9ef;
            @crust: #dce0e8;
        }
        @frappe: {
            @rosewater: #f2d5cf;
            @flamingo: #eebebe;
            @pink: #f4b8e4;
            @mauve: #ca9ee6;
            @red: #e78284;
            @maroon: #ea999c;
            @peach: #ef9f76;
            @yellow: #e5c890;
            @green: #a6d189;
            @teal: #81c8be;
            @sky: #99d1db;
            @sapphire: #85c1dc;
            @blue: #8caaee;
            @lavender: #babbf1;
            @text: #c6d0f5;
            @subtext1: #b5bfe2;
            @subtext0: #a5adce;
            @overlay2: #949cbb;
            @overlay1: #838ba7;
            @overlay0: #737994;
            @surface2: #626880;
            @surface1: #51576d;
            @surface0: #414559;
            @base: #303446;
            @mantle: #292c3c;
            @crust: #232634;
        }
        @macchiato: {
            @rosewater: #d088a6;
            @flamingo: #d088a6;
            @pink: #d088a6;
            @mauve: #b48ead;
            @red: #bf616a;
            @maroon: #bf616a;
            @peach: #d08770;
            @yellow: #ebcb8b;
            @green: #a3be8c;
            @teal: #88c0d0;
            @sky: #5e81ac;
            @sapphire: #5e81ac;
            @blue: #5e81ac;
            @lavender: #b48ead;
            @text: #eceff4;
            @subtext1: #d8dee9;
            @subtext0: #d8dee9;
            @overlay2: #d8dee9;
            @overlay1: #434c5e;
            @overlay0: #434c5e;
            @surface2: #3b4252;
            @surface1: #3b4252;
            @surface0: #3b4252;
            @base: #21252e;
            @mantle: #2e3440;
            @crust: #2e3440;
        }
        @mocha: {
            @rosewater: #f5e0dc;
            @flamingo: #f2cdcd;
            @pink: #f5c2e7;
            @mauve: #cba6f7;
            @red: #f38ba8;
            @maroon: #eba0ac;
            @peach: #fab387;
            @yellow: #f9e2af;
            @green: #a6e3a1;
            @teal: #94e2d5;
            @sky: #89dceb;
            @sapphire: #74c7ec;
            @blue: #89b4fa;
            @lavender: #b4befe;
            @text: #cdd6f4;
            @subtext1: #bac2de;
            @subtext0: #a6adc8;
            @overlay2: #9399b2;
            @overlay1: #7f849c;
            @overlay0: #6c7086;
            @surface2: #585b70;
            @surface1: #45475a;
            @surface0: #313244;
            @base: #1e1e2e;
            @mantle: #181825;
            @crust: #11111b;
        }
    }

    #catppuccin(@lookup, @accent) {
        @rosewater: @catppuccin[@@lookup][@rosewater];
        @flamingo: @catppuccin[@@lookup][@flamingo];
        @pink: @catppuccin[@@lookup][@pink];
        @mauve: @catppuccin[@@lookup][@mauve];
        @red: @catppuccin[@@lookup][@red];
        @maroon: @catppuccin[@@lookup][@maroon];
        @peach: @catppuccin[@@lookup][@peach];
        @yellow: @catppuccin[@@lookup][@yellow];
        @green: @catppuccin[@@lookup][@green];
        @teal: @catppuccin[@@lookup][@teal];
        @sky: @catppuccin[@@lookup][@sky];
        @sapphire: @catppuccin[@@lookup][@sapphire];
        @blue: @catppuccin[@@lookup][@blue];
        @lavender: @catppuccin[@@lookup][@lavender];
        @text: @catppuccin[@@lookup][@text];
        @subtext1: @catppuccin[@@lookup][@subtext1];
        @subtext0: @catppuccin[@@lookup][@subtext0];
        @overlay2: @catppuccin[@@lookup][@overlay2];
        @overlay1: @catppuccin[@@lookup][@overlay1];
        @overlay0: @catppuccin[@@lookup][@overlay0];
        @surface2: @catppuccin[@@lookup][@surface2];
        @surface1: @catppuccin[@@lookup][@surface1];
        @surface0: @catppuccin[@@lookup][@surface0];
        @base: @catppuccin[@@lookup][@base];
        @mantle: @catppuccin[@@lookup][@mantle];
        @crust: @catppuccin[@@lookup][@crust];
        @accent-colour: @catppuccin[@@lookup][@@accent];

        --yt-spec-base-background: @base;

        /* Error page */
        #error-page {
            background-color: @base !important;
        }

        #error-page-content {
            color: @text !important;
            text-shadow: none;
            background-color: @base !important;
            > p {
                color: @text !important;
                text-shadow: none;
            }
            #masthead-search-terms.masthead-search-terms-border {
                border: 1px solid @accent-colour !important;
                box-shadow: none !important;
            }
            #yt-masthead {
                color: @text !important;
                text-shadow: none;
                #logo-container {
                    .logo {
                        filter: invert(1) grayscale(1);
                    }
                    .content-region {
                        color: @text !important;
                        text-shadow: none;
                    }
                }
                #masthead-search #masthead-search-terms-border {
                    input[type="text"] {
                        color: @text !important;
                        &:focus {
                            color: @accent-colour !important;
                        }
                    }
                }
                .search-button {
                    background-color: @mantle !important;
                }
            }
        }

        * {
            scrollbar-color: @accent-colour transparent !important;
            scrollbar-width: thin !important;
            --yt-spec-text-primary: @text !important;
            --yt-spec-text-secondary: @overlay2 !important;
            --paper-spinner-color: @overlay2 !important;

            --paper-input-container-color: @overlay2 !important;

            --paper-radio-button-unchecked-color: @overlay2 !important;
            --paper-radio-button-unchecked-background-color: transparent !important;
            --paper-radio-button-checked-color: @accent-colour !important;
            --paper-radio-button-checked-ink-color: @accent-colour !important;
            --paper-radio-button-unchecked-ink-color: @accent-colour !important;

            --paper-checkbox-unchecked-color: @overlay2 !important;
            --paper-checkbox-checked-color: @accent-colour !important;
            --paper-checkbox-checkmark-color: none !important;
            --paper-checkbox-checked-ink-color: @accent-colour !important;
            --paper-checkbox-unchecked-ink-color: @accent-colour !important;
            --yt-spec-call-to-action: @accent-colour !important;
            --yt-spec-suggested-action: @surface0 !important;
            --yt-spec-outline: @surface0 !important;
            --yt-spec-brand-button-background: @accent-colour !important;
            --yt-formatted-string-bold-color: @overlay2 !important;
            --yt-spec-commerce-filled-hover: @accent-colour !important;
            --yt-spec-badge-chip-background: @mantle !important;
            --yt-spec-touch-response: @crust !important;
            --yt-spec-touch-response-inverse: @accent-colour !important;
            --yt-spec-brand-background-solid: @base !important;
            --yt-spec-text-disabled: @subtext0 !important;
            --yt-spec-button-chip-background-hover: @surface0 !important;
        }

        /* Scrollbar */
        *:not([hide-scrollbar]) ::-webkit-scrollbar-thumb {
            background: @accent-colour !important;
        }

        ::-webkit-scrollbar {
            width: 8px !important;
        }

        ::-webkit-scrollbar-track {
            background: transparent !important;
        }

        ::-webkit-scrollbar-thumb {
            background: @accent-colour !important;
        }

        /* Background */
        &,
        html,
        body,
        ytd-browse,
        ytd-watch,
        ytd-search,
        ytd-app,
        ytd-app[is-watch-page],
        [class*="ytd-watch-flexy"]:not(#engagement-panel-scrim),
        .style-scope.ytd-page-manager.hide-skeleton,
        .account-container {
            background-color: @base !important;
        }

        /* text highlight */
        ::selection {
            background: @surface0 !important;
            color: @text !important;
        }

        ::-moz-selection {
            background: @surface0 !important;
            color: @text !important;
        }

        /* links */
        a {
            outline-color: @accent-colour !important;
            &:hover {
                color: @accent-colour !important;
                fill: @accent-colour !important;
            }
        }

        /* Placeholder images */
        ytd-background-promo-renderer .image.ytd-background-promo-renderer,
        ytd-message-renderer yt-icon.ytd-message-renderer {
            color: @text !important;
        }

        /* Nav bar */
        ytd-masthead,
        #yt-masthead-container,
        #masthead-positioner-height-offset {
            background-color: @mantle !important;
            border-bottom-color: @surface0 !important;
        }

        yt-icon-button svg {
            fill: @overlay2 !important;
            &:hover {
                fill: @overlay2 !important;
            }
        }

        #logo-icon-container.ytd-topbar-logo-renderer #youtube-paths.ytd-topbar-logo-renderer path.ytd-topbar-logo-renderer,
        #youtube-red-paths {
            fill: @text !important;
        }

        /* Backgrounds */
        #like-bar.ytd-sentiment-bar-renderer,
        #progress.ytd-thumbnail-overlay-resume-playback-renderer,
        #selectionBar.paper-tabs {
            background-color: @accent-colour !important;
        }

        /* Sidebar */
        #guide-content.ytd-app,
        #contentContainer.app-drawer,
        #guide-wrapper.ytd-app {
            background: @mantle !important;
        }

        #sections.ytd-guide-renderer > .ytd-guide-renderer:not(:last-child),
        #guide-links-primary.ytd-guide-renderer,
        #guide-content.ytd-app {
            border-color: @surface0 !important;
        }

        #guide-section-title.ytd-guide-section-renderer {
            color: @text !important;
        }

        #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer {
            color: @text !important;
            &:hover {
                background-color: @surface1 !important;
                #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer,
                svg {
                    color: @accent-colour !important;
                    fill: @accent-colour !important;
                }
            }
            &[active] {
                background-color: @surface0 !important;
            }
        }

        ytd-guide-entry-renderer {
            &:hover {
                background-color: @surface1 !important;
                #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer,
                svg {
                    color: @accent-colour !important;
                    fill: @accent-colour !important;
                }
            }
            &[active] {
                background-color: @surface0 !important;
            }
        }

        #guide-links-primary,
        #guide-links-secondary {
            .ytd-guide-renderer > a {
                color: @overlay2 !important;
                &:hover {
                    color: @accent-colour !important;
                }
            }
        }

        yt-formatted-string.ytd-guide-signin-promo-renderer {
            color: @overlay2 !important;
        }

        /* Compact sidebar */
        ytd-mini-guide-renderer {
            background-color: @mantle !important;
        }

        a.ytd-mini-guide-entry-renderer {
            background-color: @mantle !important;
            &:hover,
            &:focus {
                background-color: @mantle !important;
            }
        }

        ytd-mini-guide-entry-renderer,
        a.ytd-mini-guide-entry-renderer {
            .guide-icon.ytd-mini-guide-entry-renderer,
            .title.ytd-mini-guide-entry-renderer {
                color: @overlay2 !important;
            }
            &[active],
            &:hover {
                .guide-icon.ytd-mini-guide-entry-renderer,
                .title.ytd-mini-guide-entry-renderer {
                    color: @accent-colour !important;
                }
            }
        }


        #newness-dot.ytd-guide-entry-renderer {
            background-color: @accent-colour !important;
        }

        /* featured banner */
        ytd-statement-banner-renderer {
            #text.yt-dynamic-sizing-formatted-string,
            #badge,
            #subtitle {
                color: @base !important;
            }
            .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--outline {
                color: @base !important;
                background: @accent-colour !important;
                border-color: @accent-colour !important;
            }
            .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--filled > div > yt-icon > svg > g > path {
                fill: @base !important;
            }
        }

        /* Latest YouTube posts */
        ytd-post-renderer {
            &[uses-compact-lockup],
            &[surface_="backstage-surface-type-home"] {
                background-color: @mantle !important;
                #home-content-text.ytd-post-renderer {
                    color: @text !important;
                }
            }
            &[surface_="backstage-surface-type-home"] {
                &[attachment="poll"] #vote-count-text.ytd-post-renderer {
                    color: @text !important;
                }
                #author-text.yt-simple-endpoint.ytd-post-renderer {
                    color: @accent-colour !important;
                }
            }
            &[uses-compact-lockup] {
                &[attachment="poll"] #vote-count-text.ytd-post-renderer {
                    color: @overlay2 !important;
                }
                /* vote now button */
                ytd-button-renderer.style-suggestive[is-paper-button] #button.ytd-button-renderer {
                    background-color: @accent-colour !important;
                }
            }
        }

        /* Main page video hover preview */
        #video-preview-container.ytd-video-preview {
            background: @mantle !important;
        }

        /* also other borders */
        ytd-rich-shelf-renderer,
        #show-more-button.ytd-rich-shelf-renderer,
        ytd-rich-shelf-renderer[is-show-more-hidden] #dismissible.ytd-rich-shelf-renderer,
        #contents.ytd-rich-shelf-renderer,
        ytd-compact-promoted-item-renderer[view-style="COMPACT_PROMOTED_ITEM_STYLE_RICH_GRID"] #dismissible.ytd-compact-promoted-item-renderer {
            border-color: @surface0 !important;
        }

        /* poll results */
        /* your selection */
        tp-yt-paper-item.ytd-backstage-poll-renderer {
            .progress-bar.ytd-backstage-poll-renderer {
                background-color: @surface1 !important;
            }
            &[selected] {
                .choice-info .choice-text {
                    color: @text !important;
                }
                .progress-bar.ytd-backstage-poll-renderer {
                    background-color: @accent-colour !important;
                    opacity: 0.7;
                }
            }
        }

        /* other bars */
        .progress-bar.ytd-backstage-poll-renderer {
            background-color: @mantle !important;
        }

        ytd-backstage-poll-renderer:not([is-image-poll]) tp-yt-paper-item.ytd-backstage-poll-renderer .choice-info.ytd-backstage-poll-renderer {
            border-color: transparent !important;
        }

        #title.ytd-grid-channel-renderer {
            color: @overlay2 !important;
        }

        /* Channel page */
        /* header */
        #channel-header,
        #tabs-inner-container {
            background: @base !important;
            .ytd-c4-tabbed-header-renderer {
                border-bottom-color: @surface0 !important;
            }
        }

        #channel-title.ytd-c4-tabbed-header-renderer {
            color: @text !important;
        }

        #subscriber-count.ytd-c4-tabbed-header-renderer {
            color: @overlay2 !important;
        }

        .tab-content {
            .paper-tab,
            .tp-yt-paper-tab {
                color: @text !important;
            }
        }

        paper-tab:hover .tab-content.paper-tab,
        tp-yt-paper-tab:hover > .tab-content.tp-yt-paper-tab {
            color: @accent-colour !important;
        }

        tp-yt-paper-tabs {
            --paper-tabs-selection-bar-color: @accent-colour !important;
        }

        /* search */
        .input-content.paper-input-container {
            label,
            .paper-input-label {
                color: @overlay2 !important;
            }
        }

        #subtitle,
        .style-scope.tp-yt-paper-input {
            color: @overlay2 !important;
        }

        /* arrow button */
        iron-icon {
            fill: @text !important;
            &:hover {
                fill: @accent-colour !important;
            }
        }

        .title {
            color: @text !important;
            &:hover {
                color: @accent-colour !important;
            }
        }

        #contents > .ytd-browse-secondary-contents-renderer:not(:first-child) {
            border-color: @surface0 !important;
        }

        /* Video page */
        /* title */
        .title.ytd-video-primary-info-renderer yt-formatted-string.ytd-video-primary-info-renderer {
            color: @text !important;
        }

        /* views */
        span {
            .yt-view-count-renderer,
            .ytd-video-view-count-renderer {
                color: @overlay2 !important;
            }
        }

        /* published date */
        #date.ytd-video-primary-info-renderer {
            color: @overlay2 !important;
        }

        ytd-video-primary-info-renderer {
            border-color: @surface0 !important;
        }

        .content-text.ytd-metadata-with-image-row-renderer {
            color: @text !important;
        }

        /* Share */
        paper-dialog,
        tp-yt-paper-dialog {
            background: @base !important;
        }

        #share-url {
            color: @text !important;
        }

        #copy-button:hover #text {
            color: @accent-colour !important;
        }

        #bar.yt-copy-link-renderer {
            background-color: @surface0 !important;
            border-color: @surface0 !important;
        }

        ytd-third-party-network-section-renderer ytd-copy-link-renderer.ytd-third-party-network-section-renderer,
        yt-start-at-renderer.yt-third-party-network-section-renderer {
            border-color: @surface0 !important;
        }

        yt-formatted-string.yt-start-at-renderer,
        #text.ytd-sender-id-section-renderer {
            color: @text !important;
        }

        /* share embed */
        #embed-panel.yt-sharing-embed-renderer {
            background: @mantle !important;
        }

        #close-panel-icon .yt-sharing-embed-renderer {
            color: @text !important;
            &:hover {
                color: @accent-colour !important;
            }
        }

        #title-bar,
        #additional-info {
            .yt-sharing-embed-renderer {
                border-color: @surface0 !important;
            }
        }

        #actions.ytd-reel-player-overlay-renderer,
        #action-buttons.yt-sharing-embed-renderer {
            border-color: @surface0 !important;
        }

        #privacy-mode-info.yt-sharing-embed-renderer {
            color: @overlay2 !important;
        }
        /* end share */
        /* clip */
        #container.yt-clip-creation-renderer {
            background-color: @base !important;
        }

        /* time */
        input.yt-clip-creation-scrubber-renderer {
            border-color: @overlay2 !important;
        }

        /* preview */
        .handle.yt-clip-creation-scrubber-view #handles.yt-clip-creation-scrubber-view {
            border-color: @accent-colour !important;
        }

        yt-clip-section-renderer,
        yt-clip-creation-renderer,
        yt-clip-section-footer-renderer {
            yt-button-renderer.style-primary[is-paper-button] {
                background-color: transparent !important;
            }
        }

        ytd-clip-section-footer-renderer,
        #footer.yt-clip-creation-renderer {
            background-color: @mantle !important;
        }
        /* end clip */
        #label-text.yt-dropdown-menu {
            color: @text !important;
        }

        paper-menu-button.yt-dropdown-menu:hover {
            #label-text.yt-dropdown-menu,
            #label-icon.yt-dropdown-menu svg,
            #icon-label.yt-dropdown-menu {
                color: @accent-colour !important;
                fill: @accent-colour !important;
            }
        }

        /* community */
        #message.ytd-message-renderer,
        #content-text.ytd-backstage-post-renderer,
        #repost-content-text.ytd-shared-post-renderer {
            color: @overlay2 !important;
        }

        .ytd-post-multi-image-renderer {
