            --paper-input-container-underline-color: @surface0 !important;
            --paper-input-container-underline-focus-color: @accent-colour !important;
        }

        .focused-line.tp-yt-paper-input-container {
            border-color: @accent-colour !important;
        }

        .unfocused-line.tp-yt-paper-input-container {
            border-color: @overlay2 !important;
        }

        /* comments themselves */
        #content.style-scope #content-text {
            color: @text !important;
        }

        ytd-author-comment-badge-renderer[creator] {
            background-color: @surface0 !important;
        }

        #content-text.ytd-comment-renderer,
        ytd-expander[should-use-number-of-lines][collapsed] > #content.ytd-expander {
            color: @overlay2 !important;
        }

        /* read more */
        .more-button-exp.ytd-comment-renderer,
        .less-button-exp.ytd-comment-renderer {
            color: @text !important;
            &:hover {
                color: @accent-colour !important;
            }
        }

        /* view replies */
        .more-button.style-scope.ytd-comment-replies-renderer {
            paper-button,
            yt-next-continuation,
            yt-icon {
                .ytd-comment-replies-renderer {
                    color: @text !important;
                }
            }
            &:hover {
                paper-button,
                yt-next-continuation,
                yt-icon {
                    .ytd-comment-replies-renderer {
                        color: @accent-colour !important;
                    }
                }
            }
        }

        /* comment button */
        ytd-button-renderer#submit-button.ytd-commentbox .yt-spec-button-shape-next--call-to-action.yt-spec-button-shape-next--filled {
            background-color: @accent-colour !important;
            &:hover {
                background-color: @accent-colour !important;
            }
        }

        .yt-pdg-comment-chip-renderer svg g path {
            fill: @base !important;
        }

        #comment-chip-price {
            color: @base !important;
        }

        #container.ytd-pdg-comment-preview-renderer {
            border-color: @surface0 !important;
        }

        #container.ytd-pdg-comment-preview-renderer:focus-within {
            border-color: @accent-colour !important;
        }

        /* bonus */
        #title-background.ytd-pdg-comment-preview-renderer {
            background-color: @surface0 !important;
        }

        /* For the outline around the channel name */
        .style-scope.ytd-comment-renderer.creator {
            background-color: @accent-colour !important;
        }

        .style-scope.ytd-comment-renderer.creator:hover {
            background-color: @surface0 !important;
        }

        #name.ytd-author-comment-badge-renderer {
            color: @text !important;
        }

        /* Author verified badge in comments */
        ytd-author-comment-badge-renderer:not([creator]) #icon.ytd-author-comment-badge-renderer {
            fill: @accent-colour !important;
        }

        /* Watch on YouTube */
        #offer-module {
            .ytd-watch-next-secondary-results-renderer,
            .ytd-tvfilm-offer-module-renderer {
                background-color: @mantle !important;
            }
        }

        /* Feedback */
        .uvFeedbackFormroot {
            background: @base !important;
            color: @text !important;
            .uvFeedbackFormfooter,
            .uvFeedbackFormtitle-section {
                background: @mantle !important;
            }
            .scSharedMaterialbuttonroot {
                background: @surface0 !important;
                span {
                    color: @text !important;
                }
            }
            .scSharedMaterialbuttonhairline {
                --m-btn-outline-color: @surface1 !important;
            }
            .uvFeedbackFormclose-button .scSharedMaterialbuttonroot {
                background: @mantle !important;
                &:hover {
                    background: @surface0 !important;
                }
            }
            .uvFeedbackLegaltextroot a {
                color: @accent-colour !important;
            }
        }

        /* Yotube Enhancer Extention */
        .efyt-control-bar {
            background: @surface0!important;
            * {
                fill: @text !important;
                color: @text !important;
            }
        }
        .efyt-control-bar-tooltip {
            background: @surface1!important;
            color: @text !important;
        }
        #efyt-progress::-moz-progress-bar {
            background-color: @accent-colour !important;
        }
        #efyt-close-mini-player svg {
            fill: @crust !important;
            &:hover {
                fill: @text !important;
            }
        }
    }
}

