      > lottie-component
      > svg
      > g:nth-child(2)
      > g:nth-child(2) {
      /* not liked */
      > g:nth-child(2),
      > g:nth-child(4) {
        > path:nth-child(1) {
          stroke: @text !important;
        }
      }
      /* liked */
      > g:nth-child(1),
      > g:nth-child(3) {
        > path:nth-child(1) {
          fill: @accent-Color !important;
        }
        > path:nth-child(2) {
          stroke: @accent-Color !important;
