    /* radio buttons (flair selector, etc.) */
    div[role=radio]:hover, div[role=radio]:focus {
        background: var(--accent) !important;
    }

    div[role=radio] > svg, div[role=radio] > svg:active {
        fill: var(--text) !important;
    }

    /* checkboxes */
    button[role=checkbox] {
