    }


    /* TOOLTIPS */


    div.HQ2VJViRjokXpRbJzPvvc {
        background: var(--mantle) !important;
        color: var(--text) !important;
    }

    div.HQ2VJViRjokXpRbJzPvvc::after {
        border-top-color: var(--mantle) !important;
    }
}

/* ==UserStyle==

@name YouTube Dracula
@namespace github.com/catppuccin/youtube
@homepageURL https://github.com/catppuccin/youtube
@version 2.2.3
@updateURL https://github.com/catppuccin/youtube/raw/main/src/catppuccin.user.css
@description Soothing pastel theme for YouTube
@author isabelroses
@license MIT

@preprocessor less

@var select lightTheme "Light Theme" ["latte:Latte*", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkTheme "Dark Theme" ["latte:Latte", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColour  "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red*", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Grey"]

@var checkbox joinColor "Use dark join button" 0
@var checkbox subscribeColor "Use dark subscribe button" 0
@var checkbox oledOn "Enable black bars" 0

==/UserStyle== */
@-moz-document regexp('https?:\\/\\/(?!gaming\\.)(?!consent\\.)(?!vr\\.)(?!music\\.)(?!tv\\.)(?!artists\\.)(?!studio\\.).*youtube(?!kids\\.).*\\.com\\/(?!premium|howyoutubeworks).*$') {

    /* init */
    :root[dark] {
        #catppuccin(@darkTheme, @accentColour);
        #subscribe-color(@subscribeColor);
        #join-color(@joinColor);
    }
    :root {
        #catppuccin(@lightTheme, @accentColour);
        #subscribe-color(@subscribeColor);
        #join-color(@joinColor);
    }
    #oled(@oledOn);
    @shadow: 0 1px 0.5px rgba(0, 0, 0, .13);

    /* buttons look nicer */
    #join-color(@join-color) when (@join-color =1) {
        .yt-spec-button-shape-next--mono[aria-label="Join this channel"] .yt-spec-button-shape-next--mono[aria-label="Join this channel"]:hover {
            background-color: @mantle !important;
            border-color: @mantle !important;
            color: @text !important;
        }
    }

    #subscribe-color(@subscribe-color) when (@subscribe-color =1) {
        .ytp-sb-subscribe,
        a.ytp-sb-subscribe,
        .yt-spec-button-shape-next[aria-label^="Subscribe"],
        .yt-spec-button-shape-next[aria-label^="Subscribe"]:hover {
            background-color: @mantle !important;
            border-color: @mantle !important;
            box-shadow: @shadow !important;
            color: @text !important;
        }
    }

    /* for oled monitors */
    #oled(@oled) when (@oled =1) {
        #ytd-player #container {
            background: #000 !important;
        }
    }

    @catppuccin: {
