        }
    }

    #catppuccin(@lookup, @accent) {
        @rosewater: @catppuccin[@@lookup][@rosewater];
        @flamingo: @catppuccin[@@lookup][@flamingo];
        @pink: @catppuccin[@@lookup][@pink];
        @mauve: @catppuccin[@@lookup][@mauve];
        @red: @catppuccin[@@lookup][@red];
        @maroon: @catppuccin[@@lookup][@maroon];
        @peach: @catppuccin[@@lookup][@peach];
        @yellow: @catppuccin[@@lookup][@yellow];
        @green: @catppuccin[@@lookup][@green];
        @teal: @catppuccin[@@lookup][@teal];
        @sky: @catppuccin[@@lookup][@sky];
        @sapphire: @catppuccin[@@lookup][@sapphire];
        @blue: @catppuccin[@@lookup][@blue];
        @lavender: @catppuccin[@@lookup][@lavender];
        @text: @catppuccin[@@lookup][@text];
        @subtext1: @catppuccin[@@lookup][@subtext1];
        @subtext0: @catppuccin[@@lookup][@subtext0];
        @overlay2: @catppuccin[@@lookup][@overlay2];
        @overlay1: @catppuccin[@@lookup][@overlay1];
        @overlay0: @catppuccin[@@lookup][@overlay0];
        @surface2: @catppuccin[@@lookup][@surface2];
        @surface1: @catppuccin[@@lookup][@surface1];
        @surface0: @catppuccin[@@lookup][@surface0];
        @base: @catppuccin[@@lookup][@base];
        @mantle: @catppuccin[@@lookup][@mantle];
        @crust: @catppuccin[@@lookup][@crust];
        @accent-colour: @catppuccin[@@lookup][@@accent];

        --yt-spec-base-background: @base;

        /* Error page */
        #error-page {
            background-color: @base !important;
        }

        #error-page-content {
            color: @text !important;
            text-shadow: none;
            background-color: @base !important;
            > p {
                color: @text !important;
                text-shadow: none;
            }
            #masthead-search-terms.masthead-search-terms-border {
                border: 1px solid @accent-colour !important;
                box-shadow: none !important;
            }
            #yt-masthead {
                color: @text !important;
                text-shadow: none;
                #logo-container {
                    .logo {
                        filter: invert(1) grayscale(1);
                    }
                    .content-region {
                        color: @text !important;
                        text-shadow: none;
                    }
                }
                #masthead-search #masthead-search-terms-border {
                    input[type="text"] {
                        color: @text !important;
                        &:focus {
                            color: @accent-colour !important;
                        }
                    }
                }
                .search-button {
                    background-color: @mantle !important;
                }
            }
        }

        * {
            scrollbar-color: @accent-colour transparent !important;
            scrollbar-width: thin !important;
            --yt-spec-text-primary: @text !important;
            --yt-spec-text-secondary: @overlay2 !important;
            --paper-spinner-color: @overlay2 !important;

            --paper-input-container-color: @overlay2 !important;

            --paper-radio-button-unchecked-color: @overlay2 !important;
            --paper-radio-button-unchecked-background-color: transparent !important;
            --paper-radio-button-checked-color: @accent-colour !important;
            --paper-radio-button-checked-ink-color: @accent-colour !important;
            --paper-radio-button-unchecked-ink-color: @accent-colour !important;

            --paper-checkbox-unchecked-color: @overlay2 !important;
            --paper-checkbox-checked-color: @accent-colour !important;
            --paper-checkbox-checkmark-color: none !important;
            --paper-checkbox-checked-ink-color: @accent-colour !important;
            --paper-checkbox-unchecked-ink-color: @accent-colour !important;
            --yt-spec-call-to-action: @accent-colour !important;
            --yt-spec-suggested-action: @surface0 !important;
            --yt-spec-outline: @surface0 !important;
            --yt-spec-brand-button-background: @accent-colour !important;
            --yt-formatted-string-bold-color: @overlay2 !important;
            --yt-spec-commerce-filled-hover: @accent-colour !important;
            --yt-spec-badge-chip-background: @mantle !important;
            --yt-spec-touch-response: @crust !important;
            --yt-spec-touch-response-inverse: @accent-colour !important;
            --yt-spec-brand-background-solid: @base !important;
            --yt-spec-text-disabled: @subtext0 !important;
            --yt-spec-button-chip-background-hover: @surface0 !important;
        }

        /* Scrollbar */
        *:not([hide-scrollbar]) ::-webkit-scrollbar-thumb {
            background: @accent-colour !important;
        }

        ::-webkit-scrollbar {
            width: 8px !important;
        }

        ::-webkit-scrollbar-track {
            background: transparent !important;
        }

        ::-webkit-scrollbar-thumb {
            background: @accent-colour !important;
        }

        /* Background */
        &,
        html,
        body,
        ytd-browse,
        ytd-watch,
        ytd-search,
        ytd-app,
        ytd-app[is-watch-page],
        [class*="ytd-watch-flexy"]:not(#engagement-panel-scrim),
        .style-scope.ytd-page-manager.hide-skeleton,
        .account-container {
            background-color: @base !important;
        }

        /* text highlight */
        ::selection {
            background: @surface0 !important;
            color: @text !important;
        }

        ::-moz-selection {
            background: @surface0 !important;
            color: @text !important;
        }

        /* links */
        a {
            outline-color: @accent-colour !important;
            &:hover {
                color: @accent-colour !important;
                fill: @accent-colour !important;
            }
        }

        /* Placeholder images */
        ytd-background-promo-renderer .image.ytd-background-promo-renderer,
        ytd-message-renderer yt-icon.ytd-message-renderer {
            color: @text !important;
        }

        /* Nav bar */
        ytd-masthead,
        #yt-masthead-container,
